DROP TABLE IF EXISTS tbl_admin;
CREATE TABLE tbl_admin (
  id int(11) NOT NULL AUTO_INCREMENT,
  username varchar(10) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `password` varchar(50) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  checked int(11) NOT NULL,
  PRIMARY KEY (id)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS tbl_advantage;
CREATE TABLE tbl_advantage (
  aid int(11) NOT NULL AUTO_INCREMENT,
  introduce text COLLATE utf8_bin NOT NULL,
  british_advantage text COLLATE utf8_bin NOT NULL,
  gzqz text COLLATE utf8_bin NOT NULL,
  jyzl text COLLATE utf8_bin NOT NULL,
  sjcb text COLLATE utf8_bin NOT NULL,
  fycb text COLLATE utf8_bin NOT NULL,
  yywh text COLLATE utf8_bin NOT NULL,
  kksy text COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (aid)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_advantage_index;
CREATE TABLE tbl_advantage_index (
  aid int(11) NOT NULL AUTO_INCREMENT,
  title1 varchar(100) COLLATE utf8_bin NOT NULL,
  pic1 varchar(255) COLLATE utf8_bin NOT NULL,
  introduce1 text COLLATE utf8_bin NOT NULL,
  title2 varchar(100) COLLATE utf8_bin NOT NULL,
  pic2 varchar(255) COLLATE utf8_bin NOT NULL,
  introduce2 text COLLATE utf8_bin NOT NULL,
  title3 varchar(100) COLLATE utf8_bin NOT NULL,
  pic3 varchar(255) COLLATE utf8_bin NOT NULL,
  introduce3 text COLLATE utf8_bin NOT NULL,
  title4 varchar(100) COLLATE utf8_bin NOT NULL,
  pic4 varchar(255) COLLATE utf8_bin NOT NULL,
  introduce4 text COLLATE utf8_bin NOT NULL,
  title5 varchar(100) COLLATE utf8_bin NOT NULL,
  pic5 varchar(255) COLLATE utf8_bin NOT NULL,
  introduce5 text COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (aid)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_company;
CREATE TABLE tbl_company (
  cid int(11) NOT NULL AUTO_INCREMENT,
  introduce text COLLATE utf8_bin NOT NULL,
  introduce_advpage text COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (cid)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_course;
CREATE TABLE tbl_course (
  cid int(11) NOT NULL AUTO_INCREMENT,
  syrq text COLLATE utf8_bin NOT NULL,
  picbox1 text COLLATE utf8_bin NOT NULL,
  picbox2 text COLLATE utf8_bin NOT NULL,
  picbox3 text COLLATE utf8_bin NOT NULL,
  picbox4 text COLLATE utf8_bin NOT NULL,
  picbox5 text COLLATE utf8_bin NOT NULL,
  title1 varchar(50) COLLATE utf8_bin NOT NULL,
  title2 varchar(50) COLLATE utf8_bin NOT NULL,
  title3 varchar(50) COLLATE utf8_bin NOT NULL,
  title4 varchar(50) COLLATE utf8_bin NOT NULL,
  title5 varchar(50) COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (cid)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_course_detail;
CREATE TABLE tbl_course_detail (
  cid int(11) NOT NULL AUTO_INCREMENT,
  title varchar(100) COLLATE utf8_bin NOT NULL,
  pic varchar(255) COLLATE utf8_bin NOT NULL,
  introduce text COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (cid)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_course_index;
CREATE TABLE tbl_course_index (
  cid int(11) NOT NULL AUTO_INCREMENT,
  introduce text COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (cid)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_friends;
CREATE TABLE tbl_friends (
  id int(11) NOT NULL AUTO_INCREMENT,
  friends varchar(50) COLLATE utf8_bin NOT NULL,
  url varchar(255) COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (id)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_message;
CREATE TABLE tbl_message (
  mid int(11) NOT NULL AUTO_INCREMENT,
  username varchar(50) COLLATE utf8_bin NOT NULL,
  tel varchar(50) COLLATE utf8_bin NOT NULL,
  other varchar(50) COLLATE utf8_bin NOT NULL,
  message text COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (mid)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_news;
CREATE TABLE tbl_news (
  nid int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) COLLATE utf8_bin NOT NULL,
  addtime int(11) NOT NULL,
  pic varchar(255) COLLATE utf8_bin NOT NULL,
  summary text COLLATE utf8_bin NOT NULL,
  content text COLLATE utf8_bin NOT NULL,
  `type` int(11) NOT NULL,
  PRIMARY KEY (nid)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_project;
CREATE TABLE tbl_project (
  pid int(11) NOT NULL AUTO_INCREMENT,
  pname varchar(25) COLLATE utf8_bin NOT NULL,
  introduce text COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (pid)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_project_index;
CREATE TABLE tbl_project_index (
  cid int(11) NOT NULL AUTO_INCREMENT,
  introduce text COLLATE utf8_bin,
  PRIMARY KEY (cid)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_question;
CREATE TABLE tbl_question (
  qid int(11) NOT NULL AUTO_INCREMENT,
  question text COLLATE utf8_bin NOT NULL,
  answer text COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (qid)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_service;
CREATE TABLE tbl_service (
  sid int(11) NOT NULL AUTO_INCREMENT,
  service text COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (sid)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_student;
CREATE TABLE tbl_student (
  sid int(11) NOT NULL AUTO_INCREMENT,
  stuName varchar(50) COLLATE utf8_bin NOT NULL,
  englishName varchar(50) COLLATE utf8_bin NOT NULL,
  school varchar(50) COLLATE utf8_bin NOT NULL,
  `subject` varchar(50) COLLATE utf8_bin NOT NULL,
  content text COLLATE utf8_bin NOT NULL,
  pic varchar(255) COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (sid)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_teacher;
CREATE TABLE tbl_teacher (
  tid int(11) NOT NULL AUTO_INCREMENT,
  avatar varchar(255) COLLATE utf8_bin NOT NULL,
  `name` varchar(50) COLLATE utf8_bin NOT NULL,
  job varchar(255) COLLATE utf8_bin NOT NULL,
  introduce text COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (tid)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

DROP TABLE IF EXISTS tbl_website;
CREATE TABLE tbl_website (
  id int(11) NOT NULL,
  website varchar(100) COLLATE utf8_bin NOT NULL,
  comname varchar(50) COLLATE utf8_bin NOT NULL,
  keyword varchar(255) COLLATE utf8_bin NOT NULL,
  description text COLLATE utf8_bin NOT NULL,
  negotiator varchar(50) COLLATE utf8_bin NOT NULL,
  address varchar(255) COLLATE utf8_bin NOT NULL,
  postcode varchar(10) COLLATE utf8_bin NOT NULL,
  tel varchar(20) COLLATE utf8_bin NOT NULL,
  fax varchar(20) COLLATE utf8_bin NOT NULL,
  email varchar(255) COLLATE utf8_bin NOT NULL,
  copyright varchar(255) COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (id)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

INSERT INTO tbl_admin VALUES('1','admin','21232f297a57a5a743894a0e4a801fc3','1');

INSERT INTO tbl_advantage VALUES('1','除了为有志于出国的大专生、本科生申请英国名校服务外，对于同学们的就业和未来，公司也做出了明确的规划。并且每年都提供一定的实习名额，为优秀的学子提供就业实习机会。在为学生获得更好的教育机会之后，也真正把育人为本落实到实处。','<p class=\"content\">\r\n	大不列颠及北爱尔兰联合王国（英语：The United Kingdom of Great Britain and Northern Ireland），简称联合王国（英语：United Kingdom），\r\n</p>\r\n<p class=\"content\">\r\n	中文通称英国，是由大不列颠岛上的英格兰、苏格兰、威尔士、爱尔兰岛东北部的北爱尔兰以及一系列附属岛屿共同组成的一个联合王国。\r\n</p>\r\n<p class=\"content\">\r\n	就是这样一个面积不足25万平方千米的一个岛国，面积比不上中国的一个省份，大小顶不过美国的一个州。但却在古往今来的历史长河中扮演着举足轻重的角色。\r\n</p>\r\n<p class=\"content\">\r\n	曾经不可一世的日不落帝国，在如今，依旧与美国在经济上，教育上，军事上，文化上等等发挥着不可替代的作用。\r\n</p>\r\n<p class=\"content\">\r\n	不论在中国还是其它国家和地区，英国都是诸多学子最为青睐的留学圣地。\r\n</p>','英国完全不给留学生工作机会？这个观点是绝对错误的。英国的留学签证通常为期一年半。一年的时间为全日制学生签证，在此期间，学生可以每周做20个小时以下的兼职，按照英国最低兼职工资6.4英镑/小时来计算，一个月如果工作满80个小时则至少可以获得500英镑的报酬。而在学期结束后，还有半年的全职工作签，这段期间学生可以成为英国公司的正式全职员工，工资更高，工作时间更长。在获得丰厚报酬的同时，也在英国积累了海外工作经验，对日后的个人发展来说，是一笔不可估量的财富积累。','英国拥有全面而又成熟的质量保障体系，各个学校和各个课程都要接受严格的控制、审核和评估，以保证其质量。英国所有获得国家认可的学历都由政府指定机构进行严格的质量考核，以确保学历的质量和一致性。英国拥有全世界教学及研究的整体水平和平均水平最高的大学群体。英国共有134所大学，其建校时间超过百年以上的就有30多所，其中以6所红砖大学最为典型。根据2015年QS世界大学排名，共有8所英国大学进入世界前50，18所英国大学进入世界前100，29所英国大学进入世界前200。','英国向来以学制精简著称。其它国家地区需要三四年完成的课程，在英国只需一到两年即可完成并获得学位。虽然时间短，但所学课程内容可谓有增无减，这也是不少英国留学生在学期初段抱怨学习压力大，跟不上进度的原因之一。英国本科教育只需三年即可完成。而Top up专升本课程与研究生课程更是只需一年即可完成。在节省宝贵的青春的同时，也节省了一大笔费用开支。','英国教育时常给人以十分昂贵的印象，其实事实并非如此。英国本科学费一年约为9000英镑（英镑兑人民币汇率约为1：9），研究生学费约为15000英镑。衣食住行花费一年约为8000英镑（伦敦地区稍贵，约为10000英镑）。这样算下来一年生活费加学费，约为22万人民币，而上一节提到的做兼职可补贴大部分生活费用，这样下来，一年花费只需16、17万人民币即可。','英国是英语的发源地，在英国学习，除了在自己的专业上学到世界最领先的知识外，同时，由于生活在英语世界中，随时随地都有机会练习并运用英语，让你学到纯正的英式英语。要知道，在英国的每一刻，你都等同于在国内聘请一位外教锻炼你的英语。除了语言上的提升，中西方思想的巨大差异，也会让你在与当地学生的交流过程中，在生活的点点滴滴中受益匪浅。新的环境，新的思想，会让你以新的高度去审时度势。','英国不仅是个历史悠久的国度，也是现代工业文明的发祥地。这里的每一座城市、每一座村庄都和谐地保持着现代与传统的平衡。英国拥有令人羡慕的灿烂文化和艺术，有数不清的博物馆、美术馆、歌剧院以及其他的文化设施可供流连。巨石阵的神秘，大本钟的雄伟，剑桥的浪漫，高地的壮阔，爱丁堡的鬼斧神工......这一切儿时可望不可及的名胜，在短短的留学生涯，可尽收你的眼底。英国绝大多数的景点和交通收费对国际留学生都有巨大的优惠，大家不要错过这个千载难逢的机会。除了英国境内，借留学之机踏遍欧洲大陆也是一个绝好的选择。英国位于欧洲的枢纽，是欧洲大陆的门户，在假期，可乘渡轮、火车或飞机等各种交通工具抵达欧洲各个国家、各个城市。比如从英国伦敦乘火车前往法国巴黎，只需两个半小时。');

INSERT INTO tbl_advantage_index VALUES('1','真诚的态度','upload/20151122/20151122103611_95227.jpg','对于站在人生的十字路口的你，淘优并不一味的为了业务量和金钱而鼓动你赴英深造。而是结合你的学习能力、家庭背景、个人爱好等方面对你进行客观分析，为你量身制定职业生涯规划，从而确定你是否真的适合赴英进修。','专一的业务','upload/20151122/20151122103611_21684.jpg','你可能会觉得淘优只进行英国研究生与英国Top up课程的申请，业务较单一。其实不然，这恰恰是淘优突出的地方。正是因为淘优业务的专一，才能将留英的信息掌握的如此完善，服务团队才可以如此有针对性。','无缝的对接','upload/20151122/20151122103611_04106.jpg','你可能听说过身边的师兄师姐抱怨申请进度慢，学校回复慢。与大多数只在国内设立办事处的机构不同，淘优在华在英都分别设有办事处。对于在两地都设有办事处的淘优来说，这些问题都将迎刃而解。','透明的流程','upload/20151122/20151122103611_49398.jpg','看不了文书，见不到录取这类事情绝不会在淘优这里发生。从淘优为学生上传材料的那一刻起，就会为其注册邮箱，与学生共享，实时关注申请进度。这也是淘优为什么能在广大学子那里留下良好口碑的原因之一。','独特的模式','upload/20151122/20151122103611_05639.jpg','淘优采用一对一跟到底的服务模式。从一进门就为学生咨询的顾问，将是陪伴其一路申请的人。除非学生主动提出更换负责人，否则，该顾问将一跟到底。一站式解决学生所有的申请问题。');

INSERT INTO tbl_company VALUES('1','<p>\r\n	淘优国际教育公司 （Top-U International Education）成立于英国伦敦，是一家集留学资讯服务及考试培训于一体的大型教育公司。\r\n</p>\r\n<p>\r\n	以其优秀的英国本地文案、清一色留英硕士顾问团队和独特的英国Top Up项目申请而扬名海内外。\r\n</p>','淘优国际教育公司 （Top-U International Education）成立于英国伦敦，是一家集留学资讯服务及考试培训于一体的大型教育公司。以其优秀的英国本地文案、清一色留英硕士顾问团队和独特的英国Top Up项目申请而扬名海内外。');

INSERT INTO tbl_course VALUES('1','Top Up课程是英国专门针对大专生开设的一个独特课程，又称之为续本课程，即国内大专生在完成3年的专科课程后，可以凭借在校三年取得的成绩和雅思成绩申请英国本科的课程（Top up课程）。完成学业后，可获得英国大学的学士学位文凭。进而可以凭借此文凭继续申请英国当地的研究生。','只需提交3年的专科成绩和雅思成绩，以及淘优为你准备的各种文书材料，即可完成申请。','只需一年时间便可完成Top up课程，获得学士学位。','与传统预科项目不同，完成Top up课程之后，将会获得英国当地大学的学士学位，所获学位与当地3年制本科学位完全一样，在全球都被认可。你可以直接凭此学位回国找工作或者继续在英国当地进修研究生。而完成传统预科项目后，只有一纸成绩单，没有学位证书。','与预科的低毕业率和国内专升本的低录取率不同，Top up的毕业率达90%以上，只要认真对待，一年下来，可以大大提高自己的学术水平，并且最终获得学士学位。','就读Top up课程的语言要求为雅思6分，对于你来说或许不是难事，但若无法在正式开课前达到语言要求，还可以入读学校为你开设的语言课程，待完成语言课程之后，即可正式就读Top up课程。','1.申请流程简便','2.学制精简','3.学位含金量高','4.毕业率高','5.语言桥梁');

INSERT INTO tbl_course_detail VALUES('1','研究生项目','upload/20151206/20151206105418_37789.jpg','<p class=\"content\">\r\n	你可能听说过身边的师兄师姐抱怨申请进度慢，学校回复慢。\r\n</p>\r\n<p class=\"content\">\r\n	与大多数只在国内设立办事处的机构不同，淘优在华在英都分别设有办事处。\r\n</p>\r\n<p class=\"content\">\r\n	在与学校沟通中会涉及到的诸多问题：如时差、信息更新不及时、外国网站打不开等因素而影响申请进度等等，\r\n</p>\r\n<p class=\"content\">\r\n	对于在两地都设有办事处的淘优来说，这些问题都将迎刃而解。\r\n</p>\r\n<p class=\"content\">\r\n	同时，学生抵英后，遇到的疑难杂症，也可直接联系淘优当地工作人员为你排忧解难。\r\n</p>');
INSERT INTO tbl_course_detail VALUES('2','TOP-UP课程','upload/20151206/20151206110243_54745.jpg','<p class=\"content\">你可能听说过身边的师兄师姐抱怨申请进度慢，学校回复慢。</p>\r\n				<p class=\"content\">与大多数只在国内设立办事处的机构不同，淘优在华在英都分别设有办事处。</p>\r\n				<p class=\"content\">在与学校沟通中会涉及到的诸多问题：如时差、信息更新不及时、外国网站打不开等因素而影响申请进度等等，</p>\r\n				<p class=\"content\">对于在两地都设有办事处的淘优来说，这些问题都将迎刃而解。</p>\r\n				<p class=\"content\">同时，学生抵英后，遇到的疑难杂症，也可直接联系淘优当地工作人员为你排忧解难。</p>');

INSERT INTO tbl_course_index VALUES('1','<p class=\"content\">Top Up课程是英国专门针对大专生开设的一个独特课程，</p>\r\n<p class=\"content\">又称之为续本课程，即国内大专生在完成3年的专科课程后，</p>\r\n<p class=\"content\">可以凭借在校三年取得的成绩和雅思成绩申请英国本科的课程（Top up课程）。</p>\r\n<p class=\"content\">完成学业后，可获得英国大学的学士学位文凭。</p>\r\n<p class=\"content\">进而可以凭借此文凭继续申请英国当地的研究生。</p>');

